import React from "react";

const conteudos = [
    {
      conteudo: 'High Order Functions',
      bloco: 8,
      status: 'Aprendido'
    },
    {
      conteudo: 'Composicao de Componentes',
      bloco: 11,
      status: 'Aprendendo',
    },
    {
      conteudo: 'Composicao de Estados',
      bloco: 12,
      status: 'Aprenderei'
    },
    {
      conteudo: 'Redux',
      bloco: 16,
      status: 'Aprenderei'
    },
];

class Content extends React.Component {
    render() {
        return (
            <section>
                {conteudos.map((item) => (
                  <div key={item.conteudo}>
                      <p className="card">{`O conteúdo é: ${item.conteudo}`}</p>
                      <p className="card">{`Status: ${item.status}`}</p>
                      <p className="card">{`Bloco: ${item.bloco}`}</p>
                  </div>   
                ))}
            </section>
        )
    }
}

export default Content
